export function getAuthToken(){
    const token = localStorage.getItem('token');
   return token;
}

export function tokenLoader() {
    return getAuthToken();
}

export function checkAuthLoader(){
    const token = getAuthToken();
    if(token){
        return true;
        }else{
            return false;
            }
}
